import React, { Component } from "react";
import { NavLink } from "react-router-dom";

export default class MedCalculator extends Component {
        constructor(props) {
          super(props);
          this.state = { count: 100 };
        }

    handleInc = () => this.setState(state => ({ count: state.count + 1 }));

    render() {
        return (
            <div>
                <h1>ï¼­edCalculator</h1>
                <label>Kg<input type="number" name="weight" /></label>
                <label> Age<input type="number" name="age" /></label>
                <label> Cre<input type="number" name="cre" /></label>
                <label id="CCr">{this.state.count}</label>
                <span>
                    <button onClick={this.handleInc}>+</button>
                </span>
            </div>
        );
    }
}
