import React, { Component } from "react";
import { NavLink } from "react-router-dom";

export default class MedCalculator extends Component {
        constructor(props) {
          super(props);
          this.state = { 
            ccr: 0,
            kgValue : 0
         };
        }

    handleInc = () => this.setState(state => ({ ccr: this.state.kgValue }));

    handleChange = (e) => this.setState({kgValue: e.target.value});

    render() {
        return (
            <div>
                <h1>ï¼­edCalculator</h1>
                <label>Kg<input type="number" value={this.state.kgValue} onChange={this.handleChange}/></label>
                <label> Age<input type="number" name="age" /></label>
                <label> Cre<input type="number" name="cre" /></label>
                <label id="CCr">{this.state.ccr}</label>
                <span>
                    <button onClick={this.handleInc}>calculate</button>
                </span>
            </div>
        );
    }
}
